import { Component } from "@angular/core";
import { ActivatedRoute } from "@angular/router";
import { Course } from "src/app/models/course";
import { CourseService } from "src/app/services/course.service";

@Component({
  selector: "app-course-detail",
  templateUrl: "./course-detail.component.html",
  styleUrls: ["./course-detail.component.css"],
})
export class CourseDetailComponent {
  course: Course | undefined;

  constructor(
    private route: ActivatedRoute,
    private courseService: CourseService
  ) {}

  ngOnInit(): void {
    const id = Number(this.route.snapshot.paramMap.get("id"));
    if (id) {
      this.courseService.getCourses().subscribe((courses) => {
        this.course = courses.find((c) => c.id === id);
      });
    }
  }
}
<div class="container my-5" *ngIf="course">
  <button routerLink="../" class="btn btn-secondary mb-3">← Back</button>

  <h2>{{ course.title }}</h2>
  <p><strong>Instructor:</strong> {{ course.instructor }}</p>
  <p><strong>Description:</strong> {{ course.description }}</p>
  <p><strong>Rating:</strong> {{ course.rating }} ⭐</p>

  <div class="ratio ratio-16x9 my-4">
    <video [src]="course.video" controls class="w-100 border border-dark"></video>
  </div>

  <img [src]="course.thumbnail" class="img-fluid" alt="Thumbnail" />
</div>
