import { Component } from '@angular/core';
import { FormBuilder, Validators } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

   loginForm = this.fb.group({
    email: ['', [Validators.required, Validators.email]],
    password: ['', Validators.required],
  });

  constructor(private fb: FormBuilder, private router: Router) {}

  onSubmit() {
    if (this.loginForm.invalid) {
      this.loginForm.markAllAsTouched();
      return;
    }

    const { email, password } = this.loginForm.value;
    const storedUser = JSON.parse(localStorage.getItem('loggedInUser') || '{}');

    if (storedUser?.email === email && storedUser?.password === password) {
      localStorage.setItem('isLoggedIn', 'true');
      this.router.navigate(['/']);
    } else {
      alert('Invalid credentials. Please try again.');
    }
  }

}
<div class="d-flex justify-content-center align-items-center min-vh-100 bg-light">
  <div class="card shadow p-4 col-md-5 bg-white">
    <h3 class="card-title mb-4 text-center text-primary fw-bold">Login</h3>
    <form [formGroup]="loginForm" (ngSubmit)="onSubmit()" novalidate>
      <div class="mb-3">
        <label for="email" class="form-label">Email address</label>
        <input
          type="email"
          class="form-control"
          id="email"
          placeholder="Enter your email"
          formControlName="email"
        />
        <div class="text-danger mt-1" *ngIf="loginForm.get('email')?.touched && loginForm.get('email')?.invalid">
          Valid email is required.
        </div>
      </div>

      <div class="mb-3">
        <label for="password" class="form-label">Password</label>
        <input
          type="password"
          class="form-control"
          id="password"
          placeholder="Enter your password"
          formControlName="password"
        />
        <div class="text-danger mt-1" *ngIf="loginForm.get('password')?.touched && loginForm.get('password')?.invalid">
          Password is required.
        </div>
      </div>

      <button type="submit" class="btn btn-primary w-100">Login</button>
    </form>

    <div class="text-center mt-3">
      Don't have an account? <a routerLink="/register">Create one</a>
    </div>
  </div>
</div>
