import { Component, ElementRef, HostListener, OnDestroy, OnInit, ViewChild } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Course } from '../../models/course'; // Adjust path if needed

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})

export class HomeComponent implements OnInit, OnDestroy {
  @ViewChild('track', { static: true }) trackRef!: ElementRef<HTMLElement>;
  @ViewChild('viewport', { static: true }) viewportRef!: ElementRef<HTMLElement>;

  courses: Course[] = [];
  visibleCount: number = 1;
  startIndex: number = 0;
  isTransitioning: boolean = false;
  slideInterval: any;

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.visibleCount = this.getVisibleCount();
    this.fetchCourses();
    this.startAutoSlide();
  }

  ngOnDestroy(): void {
    clearInterval(this.slideInterval);
  }

  @HostListener('window:resize')
  onResize() {
    const newVisible = this.getVisibleCount();
    if (newVisible !== this.visibleCount) {
      this.visibleCount = newVisible;
      this.renderCards();
    }
  }

  getVisibleCount(): number {
    const w = window.innerWidth;
    if (w >= 992) return 3;
    if (w >= 768) return 2;
    return 1;
  }

  fetchCourses(): void {
    this.http.get<Course[]>('assets/courses.json').subscribe({
      next: (data) => {
        this.courses = data;
        this.renderCards();
      },
      error: (err) => {
        console.error('Failed to load courses:', err);
        this.trackRef.nativeElement.innerHTML = `<div class="alert alert-danger text-center w-100">Unable to load carousel data.</div>`;
      }
    });
  }

  renderCards(): void {
    const track = this.trackRef.nativeElement;
    const total = this.courses.length;
    const displayCourses: Course[] = [];

    for (let i = 0; i < this.visibleCount; i++) {
      const index = (this.startIndex + i) % total;
      displayCourses.push(this.courses[index]);
    }

    track.innerHTML = displayCourses
      .map(
        (course) => `
        <div class="carousel-card">
          <div class="card h-100 shadow-sm">
            <img src="${course.thumbnail}" class="card-img-top" style="height: 200px; object-fit: cover; cursor: pointer;" onclick="window.openModal('${course.title}', '${course.description}')">
            <div class="card-body text-center">
              <h5 class="card-title">${course.title}</h5>
              <p class="card-text">${course.description}</p>
            </div>
          </div>
        </div>`
      )
      .join('');

    track.style.transition = 'none';
    track.style.transform = 'translateX(0)';
    track.offsetHeight;
  }

  nextSlide(): void {
    if (this.isTransitioning) return;
    this.isTransitioning = true;

    const track = this.trackRef.nativeElement;
    const moveBy = track.offsetWidth / this.visibleCount;
    const firstCard = track.children[0].cloneNode(true);
    track.appendChild(firstCard);

    track.style.transition = 'transform 0.8s ease';
    track.style.transform = `translateX(-${moveBy}px)`;

    setTimeout(() => {
      track.style.transition = 'none';
      track.removeChild(track.children[0]);
      track.style.transform = 'translateX(0)';
      this.startIndex = (this.startIndex + 1) % this.courses.length;
      this.isTransitioning = false;
    }, 800);
  }

  prevSlide(): void {
    if (this.isTransitioning) return;
    this.isTransitioning = true;

    this.startIndex = (this.startIndex - 1 + this.courses.length) % this.courses.length;
    this.renderCards();

    const track = this.trackRef.nativeElement;
    const moveBy = track.offsetWidth / (this.visibleCount + 1);

    track.style.transition = 'none';
    track.style.transform = `translateX(-${moveBy}px)`;

    setTimeout(() => {
      track.style.transition = 'transform 0.8s cubic-bezier(0.4, 0, 0.2, 1)';
      track.style.transform = 'translateX(0)';
    }, 50);

    setTimeout(() => {
      this.isTransitioning = false;
    }, 850);
  }

  startAutoSlide(): void {
    this.slideInterval = setInterval(() => this.nextSlide(), 2500);

    const viewport = this.viewportRef.nativeElement;
    viewport.addEventListener('mouseenter', () => clearInterval(this.slideInterval));
    viewport.addEventListener('mouseleave', () => this.startAutoSlide());
  }
}


