import { Component, ElementRef, HostListener, OnInit, ViewChild } from '@angular/core';
import { Course } from '../../models/course';
import { Testimonial } from '../../models/testimonial';
import { CourseService } from '../../services/course.service';
import { TestimonialService } from '../../services/testimonial.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent implements OnInit {
  courses: Course[] = [];
  visibleCourses: Course[] = [];
  visibleCount: number = this.getVisibleCount();
  startIndex: number = 0;
  isTransitioning: boolean = false;

  testimonials: Testimonial[] = [];
  currentTestimonial: number = 0;

  testimonialIntervalId: any;
  slideIntervalId: any;

  @ViewChild('track') trackRef!: ElementRef;


  constructor(
    private courseService: CourseService,
    private testimonialService: TestimonialService
  ) {}

  ngOnInit(): void {
    this.loadCourses();
    this.loadTestimonials();
    this.setupAutoSlide();
  }

  ngOnDestroy(): void {
    clearInterval(this.slideIntervalId);
    clearInterval(this.testimonialIntervalId);
  }

  private getVisibleCount(): number {
    const width = window.innerWidth;
    if (width >= 992) return 3;
    if (width >= 768) return 2;
    return 1;
  }

  private loadCourses(): void {
    this.courseService.getCourses().subscribe({
      next: (data: Course[]) => {
        this.courses = data;
        this.updateVisibleCourses();
      },
      error: () => {
        console.error('Failed to load courses');
      }
    });
  }

  private loadTestimonials(): void {
    this.testimonialService.getTestimonials().subscribe({
      next: (data: Testimonial[]) => {
        this.testimonials = data;
        this.testimonialIntervalId = setInterval(() => {
          this.currentTestimonial = (this.currentTestimonial + 1) % this.testimonials.length;
        }, 3000);
      },
      error: () => {
        console.error('Failed to load testimonials');
      }
    });
  }

  private setupAutoSlide(): void {
    this.slideIntervalId = setInterval(() => this.nextSlide(), 2500);
  }

  @HostListener('window:resize')
  onResize(): void {
    const newVisibleCount = this.getVisibleCount();
    if (newVisibleCount !== this.visibleCount) {
      this.visibleCount = newVisibleCount;
      this.updateVisibleCourses();
    }
  }

  updateVisibleCourses(): void {
    this.visibleCourses = [];
    for (let i = 0; i < this.visibleCount; i++) {
      const index = (this.startIndex + i) % this.courses.length;
      this.visibleCourses.push(this.courses[index]);
    }
  }

  nextSlide(): void {
    if (this.isTransitioning || this.courses.length === 0) return;
    this.isTransitioning = true;

    const track = this.trackRef.nativeElement;
    const moveBy = track.offsetWidth / this.visibleCount;

    // Clone first card
    const firstCard = track.children[0].cloneNode(true) as HTMLElement;
    track.appendChild(firstCard);

    // Slide left
    track.style.transition = 'transform 0.8s ease';
    track.style.transform = `translateX(-${moveBy}px)`;

    setTimeout(() => {
      track.style.transition = 'none';
      track.style.transform = 'translateX(0)';
      track.removeChild(track.children[0]);

      this.startIndex = (this.startIndex + 1) % this.courses.length;
      this.updateVisibleCourses();

      this.isTransitioning = false;
    }, 800);
  }

  prevSlide(): void {
    if (this.isTransitioning || this.courses.length === 0) return;
    this.isTransitioning = true;

    this.startIndex = (this.startIndex - 1 + this.courses.length) % this.courses.length;
    this.updateVisibleCourses();

    const track = this.trackRef.nativeElement;
    const moveBy = track.offsetWidth / (this.visibleCount + 1);

    track.style.transition = 'none';
    track.style.transform = `translateX(-${moveBy}px)`;

    setTimeout(() => {
      track.style.transition = 'transform 0.8s ease';
      track.style.transform = 'translateX(0)';
    }, 50);

    setTimeout(() => {
      this.isTransitioning = false;
    }, 850);
  }


  updateTrackPosition(animate: boolean = false): void {
    const track = this.trackRef.nativeElement as HTMLElement;
    const cardWidth = track.children[0]?.clientWidth || 320; // fallback
    const gap = 16; // from .mx-2
    const offset = (cardWidth + gap * 2) * this.startIndex;

    if (animate) {
      track.style.transition = 'transform 0.8s ease';
    } else {
      track.style.transition = 'none';
    }

    track.style.transform = `translateX(-${offset}px)`;
  }
}
<!-- Hero Section -->
<section class="hero-section text-white text-center d-flex align-items-center">
  <div class="container py-5">
    <h1 class="display-4 fw-bold">Empower Your Learning with EduSphere</h1>
    <p class="lead">
      Join thousands of learners and explore diverse online courses from expert
      instructors.
    </p>
    <a routerLink="courses" class="btn btn-light btn-lg mt-3"
      >Explore Courses</a
    >
  </div>
</section>

<!-- Course Categories Section -->
<section class="bg-white position-relative z-1">
  <div class="container py-5 position-relative text-center">
    <!-- Left Vertical Line -->
    <div
      class="position-absolute top-50 start-0 translate-middle-y d-none d-md-block ps-4"
    >
      <div class="vertical-line bg-primary rounded"></div>
    </div>

    <!-- Right Vertical Line -->
    <div
      class="position-absolute top-50 end-0 translate-middle-y d-none d-md-block pe-4"
    >
      <div class="vertical-line bg-primary rounded"></div>
    </div>

    <!-- Heading -->
    <h2 class="mb-5 fw-bold text-primary position-relative z-1">
      Course Category
    </h2>

    <!-- Category Cards -->
    <div class="row justify-content-center g-4 position-relative z-1">
      <!-- Web Dev -->
      <div class="col-lg-3 col-md-4 col-sm-6">
        <div class="card border-0 shadow h-100">
          <div class="ratio ratio-4x3">
            <img
              src="https://static.vecteezy.com/system/resources/previews/002/042/858/original/landing-page-template-with-businesspeople-building-a-modern-website-vector.jpg"
              class="card-img-top object-fit-cover"
              alt="Web Dev"
            />
          </div>
          <div class="card-body d-grid p-0">
            <button class="btn btn-primary rounded-0 py-2">
              Web Development
            </button>
          </div>
        </div>
      </div>

      <div class="col-lg-3 col-md-4 col-sm-6">
        <div class="card border-0 shadow h-100">
          <div class="ratio ratio-4x3">
            <img
              src="https://www.milesweb.com/blog/wp-content/uploads/2021/06/10-Best-Customer-Onboarding-Strategies-That-Can-Work-Wonders-For-Your-Business.jpg"
              class="card-img-top object-fit-cover"
              alt="Data Science"
            />
          </div>
          <div class="card-body d-grid p-0">
            <button class="btn btn-primary rounded-0 py-2">Data Science</button>
          </div>
        </div>
      </div>

      <!-- Design -->
      <div class="col-lg-3 col-md-4 col-sm-6">
        <div class="card border-0 shadow h-100">
          <div class="ratio ratio-4x3">
            <img
              src="https://www.specbee.com/sites/default/files/2021-02/Drupal-for-everyone-teaser-min.jpg"
              class="card-img-top object-fit-cover"
              alt="Design"
            />
          </div>
          <div class="card-body d-grid p-0">
            <button class="btn btn-primary rounded-0 py-2">Design</button>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>

<section class="bg-light">
  <div class="container my-5 bg-light">
    <h2 class="mb-5 fw-bold text-primary text-center">Top Rated Courses</h2>
    <div class="position-relative">
      <button
        class="btn btn-dark position-absolute top-50 start-0 translate-middle-y z-3"
        (click)="prevSlide()"
      >
        &larr;
      </button>
      <button
        class="btn btn-dark position-absolute top-50 end-0 translate-middle-y z-3"
        (click)="nextSlide()"
      >
        &rarr;
      </button>

      <div class="overflow-hidden w-100">
        <div class="d-flex justify-content-center flex-nowrap transition-track" #track>
          <div
            class="card mx-2 shadow"
            style="min-width: 300px; max-width: 320px"
            *ngFor="let course of courses; let i = index"
          >
            <div class="ratio ratio-16x9">
              <img
                [src]="course.thumbnail"
                class="img-fluid rounded object-fit-cover"
                [alt]="course.title"
              />
            </div>
            <div class="card-body text-center">
              <h5 class="card-title">{{ course.title }}</h5>
              <p class="card-text">{{ course.description }}</p>
              <span class="badge bg-success">
                <i class="bi bi-star-fill"></i> {{ course.rating }}
              </span>
            </div>

          </div>
        </div>
      </div>
    </div>
  </div>
</section>

<div id="testimonial-section" class="container mt-5 mb-5">
  <h2 class="mb-5 fw-bold text-primary text-center">What Our Students Say</h2>
  <div class="testimonial-slider bg-white p-4 rounded shadow text-center">
    <p id="testimonial-text" class="mb-2">{{ testimonials[currentTestimonial].text }}</p>
    <h6 id="testimonial-author">{{ testimonials[currentTestimonial].author }}</h6>
  </div>
</div>
